mapper.select:
SELECT code, fid, fsign, fpath, fname
FROM file_table
WHERE fid = #{atchFileId}

SELECT COUNT(fid)
FROM file_table
WHERE fid = #{fid}

SELECT COUNT(fsign)
FROM file_table
WHERE fid = #{atchFileId}

mapper.update:

UPDATE	tb_${siteCode}
SET		atchFileId = ""
WHERE 	code = #{b_fileCode}


UPDATE	file_table
SET		fsign = #{fsign}
WHERE 	code = #{code}

mapper.delete:

