mapper.select:
SELECT siteCode, maxFileUploadNumber, fileUploadType
FROM SITE_TABLE
WHERE siteCode = #{siteCode}

mapper.update:

UPDATE	SITE_TABLE

SET
	title = #{title}
	,adminName=#{adminName}
	,formCode = #{formCode}
	,templateType  = #{templateType}
	,maxFileUploadNumber  = #{maxFileUploadNumber}
	,fileUploadType  = #{fileUploadType}
	,placeRow  = #{placeRow}
	,placeWidth  = #{placeWidth}
	,lastUpdtPnttm=now()

WHERE siteCode = #{siteCode}

UPDATE SITE_TABLE SET
	placeName = #{placeName}
WHERE siteCode = #{siteCode}

UPDATE SITE_TABLE SET
	del_chk = 'Y'
WHERE siteCode = #{siteCode}

UPDATE SITE_TABLE SET
	del_chk = 'N'
WHERE siteCode = #{siteCode}

mapper.select:
SELECT code, formName FROM TEMPLATEFIELD_TABLE WHERE
1 = 1
code = #{code}
