mapper.select:


        SELECT CODE,






		        	DATE_FORMAT(${item}, '%Y-%m-%d') AS ${item}




		        	${item}






		FROM	TB_${siteCode}
		WHERE	1=1


				AND (TITLE LIKE CONCAT ('%', #{searchWrd},'%') OR CONTEXT LIKE CONCAT ('%', #{searchWrd},'%') )  


				TITLE LIKE CONCAT('%',#{searchWrd} ,'%')  


				CONTEXT LIKE CONCAT('%',#{searchWrd} ,'%')  


		ORDER BY frstRegistPnttm DESC,  code DESC
	    LIMIT ${pageIndex}, #{recordCountPerPage}


SELECT atchFileId
FROM TB_${siteCode}
WHERE CODE = #{code}


SELECT count(atchFileId)
FROM TB_${siteCode}
where atchfileid != ""

mapper.update:

UPDATE	TB_${siteCode}

SET
	title = #{title}
	,context=#{context}
	,writerID = #{writerID}
	,atchFileId = #{atchFileId}
	,frstRegistNm  = #{frstRegistNm}
	,lastUpdtNm  = "수정자"
	,lastUpdtPnttm=now()

WHERE code = #{code}
