mapper.select:


        SELECT CODE,






		        	DATE_FORMAT(${item}, '%Y-%m-%d') AS ${item}




		        	${item}






		FROM	TB_${siteCode}
		WHERE	1=1


				AND (TITLE LIKE CONCAT ('%', #{searchWrd},'%') OR CONTEXT LIKE CONCAT ('%', #{searchWrd},'%') )  


				AND TITLE LIKE CONCAT('%',#{searchWrd} ,'%')  


				AND CONTEXT LIKE CONCAT('%',#{searchWrd} ,'%')  


		ORDER BY frstRegistPnttm DESC,  code DESC
	    LIMIT ${pageIndex}, #{recordCountPerPage}


SELECT atchFileId
FROM TB_${siteCode}
WHERE CODE = #{code}


SELECT Max(atchFileId)
FROM TB_${siteCode}
where atchfileid != ""


SELECT count(atchFileId)
FROM TB_${siteCode}
where atchfileid != ""
